<?phpinclude_once(dirname(__FILE__) . '/../../model/include.php');include_once(dirname(__FILE__) . '/../../auth.php');if ($_POST['option'] == 'delete') {    $BLOG = new Blog();    $BLOG_PHOTO = new BlogPhoto();    $ARR = $BLOG->getterAll($_POST['id']);    unlink('../../../upload/blog/' . $ARR['image']);    unlink('../../../upload/blog/original/' . $ARR['image']);    foreach ($BLOG_PHOTO->getPhotosByBlogId($_POST['id']) as $blog_photo) {        $BLOG_PHOTO->setId($blog_photo['id']);        $BLOG_PHOTO->delete();    }    $result = $BLOG->delete();    if ($result) {        $result = ["status" => "true"];        header('Content-type: application/json');        echo json_encode($result);    }}if ($_POST['option'] == 'delete-photo') {    $BLOG_PHOTO = new BlogPhoto();    $BLOG_PHOTO->setId($_POST['id']);    $result = $BLOG_PHOTO->delete();    if ($result) {        $result = ["status" => "true"];        header('Content-type: application/json');        echo json_encode($result);    }}