<?phpinclude_once(dirname(__FILE__) . '/../../model/include.php');include_once(dirname(__FILE__) . '/../../auth.php');if ($_POST['option'] == 'delete') {    $PROJECT = new Project();    $PROJECT_PHOTO = new ProjectPhoto();    $ARR = $PROJECT->getterAll($_POST['id']);    unlink('../../../upload/project/' . $ARR['image']);    foreach ($PROJECT_PHOTO->getPhotosByProjectId($_POST['id']) as $project_photo){          $PROJECT_PHOTO->setId($project_photo['id']);          $PROJECT_PHOTO->delete();    }      $result = $PROJECT->delete();    if ($result) {        $result = ["status" => "true"];        header('Content-type: application/json');        echo json_encode($result);    }}if ($_POST['option'] == 'delete-photo') {    $PROJECT_PHOTO = new ProjectPhoto();    $PROJECT_PHOTO->setId($_POST['id']);    $result = $PROJECT_PHOTO->delete();    if ($result) {        $result = ["status" => "true"];        header('Content-type: application/json');        echo json_encode($result);    }}