<?phpinclude_once(dirname(__FILE__) . '/../../model/include.php');include_once(dirname(__FILE__) . '/../../auth.php');if ($_POST['option'] == 'delete') {    $SERVICE = new Service();    $SERVICE_PHOTO = new ServicePhoto();    $ARR = $SERVICE->getterAllById($_POST['id']);    unlink('../../../upload/service/' . $ARR['image']);    unlink('../../../upload/service/icon/' . $ARR['icon']);    foreach ($SERVICE_PHOTO->getPhotosByServiceId($_POST['id']) as $service_photo){          $SERVICE_PHOTO->setId($service_photo['id']);          $SERVICE_PHOTO->delete();    }      $result = $SERVICE->delete();    if ($result) {        $result = ["status" => "true"];        header('Content-type: application/json');        echo json_encode($result);    }}if ($_POST['option'] == 'delete-photo') {    $SERVICE_PHOTO = new ServicePhoto();    $SERVICE_PHOTO->setId($_POST['id']);    $result = $SERVICE_PHOTO->delete();    if ($result) {        $result = ["status" => "true"];        header('Content-type: application/json');        echo json_encode($result);    }}