<?phpclass User_Login_Model extends Model{    function __construct()    {        parent::__construct();    }    function login($data)    {        $result = $this->db->select(array(            'table' => LOGIN_CONFIG['TABLE'] , 'column' => '*',            'where' => LOGIN_CONFIG['USERNAME']['COLUMN'] . ' = :username and is_active = :is_active and is_deleted = :is_deleted',            'data' => array('username' => $data, 'is_active' => 1, 'is_deleted' => 0)        ));        return $result;    }    function set_login_token($id, $login_token)    {        $result = $this->db->update(            // array(            //     'table' => LOGIN_CONFIG['TABLE'],            //     'where' => 'id = :id',            //     'data' => array('id' => $id),            //     'set' => array('token' => $login_token, 'try' => '0')            // )            array(                'table' => LOGIN_CONFIG['TABLE'],                'where' => 'id = :id',                'data' => array('id' => $id, 'token' => $login_token),                'set' => 'token = :token, try = 0'            )        );        return $result;    }    function by_username($username)    {        $result = $this->db->select(array(            'table' => LOGIN_CONFIG['TABLE'],            'column' => '*',            'where' => LOGIN_CONFIG['USERNAME']['COLUMN'] . ' = :username',            'data' => array('username' => $username)        ));        return $result;    }    function update_try($id, $try)    {        $result = $this->db->update(            // array(            //     'table' => LOGIN_CONFIG['TABLE'],            //     'set' => ['try' => ' :try'],            //     'where' => 'id = :id',            //     'data' => array('id' => $id, 'try' => $try)            // )            array(                'table' => LOGIN_CONFIG['TABLE'],                'set' => 'try = :try',                'where' => 'id = :id',                'data' => array('id' => $id, 'try' => $try)            )        );        return $result;    }    function deactivate($id)    {        $result = $this->db->update(            // array(            //     'table' => LOGIN_CONFIG['TABLE'],            //     'set' => ['is_active' => ' :is_active'],            //     'where' => 'id = :id',            //     'data' => array('id' => $id, 'is_active' => '0')            // )            array(                'table' => LOGIN_CONFIG['TABLE'],                'set' => 'is_active = :is_active',                'where' => 'id = :id',                'data' => array('id' => $id, 'is_active' => '0')            )        );        return $result;    }    function logout($id)    {        $result = $this->db->update(            // array(            //     'table' => LOGIN_CONFIG['TABLE'],            //     'set' => ['token' => ' :token'],            //     'where' => 'id = :id',            //     'data' => array('id' => $id, 'token' => rand(100000, 999999))            // )            array(                'table' => LOGIN_CONFIG['TABLE'],                'set' => 'token = :token',                'where' => 'id = :id',                'data' => array('id' => $id, 'token' => rand(100000, 999999))            )        );        return $result;    }}